INSERT INTO DETECTION_GUIDANCE (mqe_code, how_to_fix, why_to_fix) VALUES ('MQE0600', 'Contact the software vendor and request that when sending a guardian/parent address to ensure that the address type is indicated with a valid address type code. ', 'Some IIS might not read the address correctly unless the address type is indicated properly. ');
